import React, { Component } from 'react'
import { Link } from "react-router-dom"
import LoginModal from "../../LoginModal/LoginModal";

import "./Actions.css";
import Button from "../../button/Button";

import PersonIcon from "@material-ui/icons/PersonOutline"
import ArrowDropDownIcon from "@material-ui/icons/ArrowDropDown";
import { SettingsPowerRounded } from "@material-ui/icons";
import App from "../../App";

export default class Actions extends Component {
	state = {
		modalOpen: false
	}

	handleModalOpen = () => {
		this.setState((prevState) => {
			return {
				modalOpen: !prevState.modalOpen
			}
		})
	}

	render() {
		return (
			<div className="actions">
			<Button onClick={this.handleModalOpen}  type="button" buttonStyle="btn--secondary" buttonSize="btn--small">Log In</Button>
            <Button type="button" buttonStyle="btn--primary" buttonSize="btn--small"><Link to="/register">Sign Up</Link></Button>
			<div className="profile ">
                <PersonIcon className="hoverable"/>
                <ArrowDropDownIcon className="hoverable"/>
            </div>
			<LoginModal
				modalOpen={this.state.modalOpen}
				handleModalOpen={this.handleModalOpen}
			/>
		</div>
		)
	}
}
