import axios from "axios";
import { Redirect, useHistory } from "react-router-dom";

// Create Post

export const createPost = (user, title, body) => {

    let history = useHistory();

	console.log("Create post function executed.");

    console.log("user id:", user);
	console.log("title is:", title);
	console.log("body is:", body);

	const config = {
		headers: {
			"Content-Type": "application/json",
		},
	};

	// Request Body
	const requestBody = JSON.stringify({ user, title, body });

	axios
		.post("http://127.0.0.1:8000/api/post-submit/", requestBody, config)
		.then((res) => {
			console.log(res)
            history.push('/homepage')
		})
		.catch((err) => console.log(err));
};
