import React, { useState, useEffect, Fragment } from "react";
import { useSelector, useDispatch, connect } from 'react-redux'

import Pagination from "../../../../pagination/Pagination";
import LoginModal from "../../../../loginModal/LoginModal";


import { getPosts, likePost } from '../../../../../actions/posts';

import "./posts.css";
import ArrowUpwardIcon from "@material-ui/icons/ArrowUpward";
import ArrowDownwardIcon from "@material-ui/icons/ArrowDownward";

export function Posts(isAuthenticated) {

    const [currentPage, setCurrentPage] = useState(1);
	const [postsPerPage] = useState(10);

    const posts = useSelector((state) => state.posts.posts)

    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(getPosts())
    }, [])

    const paginate = (pageNumber) => setCurrentPage(pageNumber);

   // Get current post (for paggination)
	const indexOfLastPost = currentPage * postsPerPage;
	const indexOfFirstPost = indexOfLastPost - postsPerPage;
	const currentPosts = posts.slice(indexOfFirstPost, indexOfLastPost);

    // Handle Modal
    // Handle State for Modal (checks if user clicked on login or logout)

	const [signInState, setsignInState] = useState({
		open: false,
		signInType: "",
	});

	// setSignInState to closed if the user is authenticated

	useEffect(() => {
		setsignInState({ open: false });
	}, []);


	const handleRegister = () => {
		setsignInState({
			open: true,
			signInType: "register",
		});
	};

    // Check 

    if (isAuthenticated) {
        return (
            <div>
                The user is authenticated
            </div>
        )
    } else {
        return (
            <Fragment>
                    {currentPosts.map(post => (
                        <div className="post-wrapper"> 
                            <div className="post">
                                <div className="post-sidebar">
                                    <ArrowUpwardIcon onClick={handleRegister} className="upvote" />
                                    <div>{post.likes !==0 ? post.likes : "Vote"}


                                    </div>
                                    <ArrowDownwardIcon  onClick={handleRegister} className="downvote" />
                                </div>
                                <div className="post-mainbar">
                                    <div className="post-author">
                                            Posted by user: {post.user}
                                    </div>
                                    <div className="post-title">
                                        {post.title}
                                    </div>
                                    <div className="post-body">
                                        {post.body}
                                    </div>
                                    <div className="post-footer">
                                        <div className="comments"> Comments</div>
                                    </div>
                                </div>

                            </div>
                        </div>
                    ))}
                    <LoginModal
                        open={signInState.open}
                        action={signInState.signInType}
                        onClose={() => setsignInState({ open: false })}
                    ></LoginModal>
                    <Pagination postsPerPage={postsPerPage} totalPosts={posts.length} paginate={paginate}/>
                </Fragment>
        )
    }
}

function mapStateToProps(state) {
	return {
		state,
		isAuthenticated: state.auth.isAuthenticated,
	};
}

export default connect(mapStateToProps)(Posts);
