import React from 'react';

const MODAL_STYLES = {
    position: 'fixed',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    backgroundColor: '#FFF',
    padding: '50px', 
    zIndex: 1000
}

export default function LoginModal({ open, children, onClose}) {
    if (!open) return null

    return (
        <>
            <div style={OVERLAY_STYLE}/>
                <div style={MODAL_STYLES}>
                    <button onClick={onClose}>Close Modal</button>
                    {children}
                </div>
        </>
    )
}
