import axios from "axios";
import { CREATE_POST } from "./types";

// Test Function 

export function testFunction (title, body) {
    return (
        console.log("The test function executed"),
        console.log("title is:", title),
        console.log("body is:", body),

        "This is a bloody test function")
}

export const testFunctionNew = (title, body)  => {
    console.log("The  SECOMD test function executed")
    console.log("title is:", title)
    console.log("body is:", body)
};

// Create Post

export const createPost = (title, body) => {
	console.log("Create post function executed.");

	console.log("title is:", title);
	console.log("body is:", body);

	const config = {
		headers: {
			"Content-Type": "application/json",
		},
	};

	// Request Body
	const requestBody = JSON.stringify({ title, body });

	axios
		.post("http://127.0.0.1:8000/api/post-submit/", requestBody, config)
		.then((res) => {
			console.log(res)
		})
		.catch((err) => console.log(err));
};
