import React, { useState } from "react";

import "./Pagination.css"

export default function Pagination({postsPerPage, totalPosts, paginate}) {
    const pageNumbers = [];

    const [currentPage, setCurrentPage] = useState(1);

    for (let i = 1; i <= Math.ceil(totalPosts/postsPerPage); i++) {
        pageNumbers.push(i);
    }

    return (

        <div className="pagination-container">
            { pageNumbers.length == 1 ?
                <div>
                    {currentPage}
                </div>
            :
            currentPage === 1 ? 
                <div>
                    {currentPage}
                    <button onClick={() => {paginate(currentPage + 1); setCurrentPage(currentPage + 1)}} href='#' className="">Next</button>
                </div>
            :
            currentPage == pageNumbers.length ?
            <div>
                <button onClick={() => {paginate(currentPage - 1); setCurrentPage(currentPage - 1)}} href='#'>Previous</button>
                    {currentPage}
            </div>
            :
            <div>
                <button onClick={() => {paginate(currentPage - 1); setCurrentPage(currentPage - 1)}} href='#'>Previous</button>
                    {currentPage}
                <button onClick={() => {paginate(currentPage + 1); setCurrentPage(currentPage + 1)}} href='#'>Next</button>
            </div>
            }
        </div>
    )

}
