import React, {useEffect} from "react";
import { useForm } from "react-hook-form";
import { Redirect } from "react-router-dom";
import { createPost } from "../../actions/createPost";

import "./Create.css"

export default function Create() {
	const { register, handleSubmit, formState: { errors }} = useForm();

    const testFunction = () => {
        console.log("The test function executed")
        return ("This is a bloody test function")
    }

	const onSubmit = (data) => {
		console.log(data); // Check form data in the console. 


        // Call the Create Post function
        testFunction()

        createPost("title", "")

        testFunction()

        // Redirect to homepage
        return <Redirect to="/homepage"/>

	};

	return (
		<div className="create-container">
			<h3>Create Post</h3>
			<hr />
            <div className="form-container">
                <form className="form-group" onSubmit={handleSubmit(onSubmit)}>
                    <input
                        className="title-input"
                        type="text" 
                        placeholder="Title" 
                        name="title"
                        {...register("title", { required: true })}
                    />
                    {errors.titleRequired && <span>You must provide a title</span>}
                    <textarea
                        className="body-input"
                        type="text"
                        placeholder="Text(Optional)"
                        name="body"
                        {...register("body")} 
                    />
                    <input
                        className="form-button"
                        value="POST"
                        type="submit"
                    />
                </form>
            </div>
		</div>
	);
}
