import React, { useState, useEffect, Fragment } from "react";
import { useSelector, useDispatch } from 'react-redux'

import Pagination from "../../../../pagination/Pagination";


import { getPosts, likePost } from '../../../../../actions/posts';

import "./posts.css";
import ArrowUpwardIcon from "@material-ui/icons/ArrowUpward";
import ArrowDownwardIcon from "@material-ui/icons/ArrowDownward";

export default function Posts() {

    const [currentPage, setCurrentPage] = useState(1);
	const [postsPerPage] = useState(10);

    const posts = useSelector((state) => state.posts.posts)

    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(getPosts())
    }, [])

    const paginate = (pageNumber) => setCurrentPage(pageNumber);

   // Get current post (for paggination)
	const indexOfLastPost = currentPage * postsPerPage;
	const indexOfFirstPost = indexOfLastPost - postsPerPage;
	const currentPosts = posts.slice(indexOfFirstPost, indexOfLastPost);

    // Like post



    return (
        <Fragment>
                {currentPosts.map(post => (
                    <div className="post-wrapper"> 
                        <div className="post">
                            <div className="post-sidebar">
                                <ArrowUpwardIcon onClick={likePost(post)} className="upvote" />
                                <div>{post.likes !==0 ? post.likes : "Vote"}


                                </div>
                                <ArrowDownwardIcon className="downvote" />
                            </div>
                            <div className="post-mainbar">
                                <div className="post-author">
                                        Posted by user: {post.user}
                                </div>
                                <div className="post-title">
                                    {post.title}
                                </div>
                                <div className="post-body">
                                    {post.body}
                                </div>
                                <div className="post-footer">
                                    <div className="comments"> Comments</div>
                                </div>
                            </div>

                        </div>
                    </div>
                ))}
                <Pagination postsPerPage={postsPerPage} totalPosts={posts.length} paginate={paginate}/>
            </Fragment>
    )
}
