import axios from "axios";
import { CREATE_POST } from "./types";

// Create Post

export const createPosts = (title, body)  => dispatch => {

    const config = {
        headers : {
            "Content-Type": "application/json"
        }
    }

    // Request Body 
    const requestBody = JSON.stringify({title, body})

    axios
        .get('http://127.0.0.1:8000/api/post-submit/', requestBody, config)
        .then(res => {
            dispatch({
                type: CREATE_POST,
                payload: res.data
            });

        }).catch(err => console.log(err));
}
