import axios from "axios";
import { GET_POSTS, CREATE_POST, LIKE_POST, GET_FOLLOWING_POSTS, GET_USER_POSTS } from "./types";
import { tokenConfig } from './auth';


// Follow or Unfollow user

export const followUnfollow = (user_id, id, action) => (getState) => {

    axios
        .post(`http://127.0.0.1:8000/api/follow-user/${user_id}/${id}`, { action: action }, tokenConfig(getState))
        .then(res => {
            console.log(res.data)
        })
        .catch(err => console.log(err));
};
