import React from 'react';
import reactDom from "react-dom"
import Login from '../accounts/Login';
import Register from '../accounts/Register';

import './LoginModal.css'
import { CgClose } from 'react-icons/all'

export default function LoginModal({ open, action, onClose, onSubmit}) {

    useEffect(() => {
        effect
        return () => {
            cleanup
        }
    }, [action])


    if (!open) return null

    if (action === "login") {

        return reactDom.createPortal(
            <>
                    <div className="overlay-style">
                        <div className="modal-style">
                            <div className="modal-container">
                                <CgClose className="exit-button" size={30} onClick={onClose}/>
                                <div className="modal-side-bar"></div>
                                <div className="modal-login">
                                    <Login/>
                                </div>
                                <p>
                                    New to Reddit? <b onClick={() => {action="register"; console.log(action)}}>SIGN UP</b>
                                </p>
                                {console.log(action)}
                            </div>
                        </div>
                    </div>
            </>, 
            document.getElementById('portal')
        )
    }
    else if (action === "register") {
        return reactDom.createPortal(
            <>

                <div className="overlay-style">
                    <div className="modal-style">
                        <div className="modal-container">
                            <CgClose className="exit-button" size={30} onClick={onClose}/>
                            <div className="modal-side-bar"></div>
                            <div className="modal-login">
                                <Register/>
                            </div>
                            <p>
                                Already have an account? <b onClick={() => action="login"}>Log In</b>
                            </p>
                            {console.log(action)}
                        </div>
                    </div>
                </div>
            </>, 
            document.getElementById('portal')
        )

    }

}
