import axios from "axios";
import { tokenConfig } from './auth';

import { GET_POSTS, CREATE_POST } from "./types";

//Get Posts

export const getPosts = () => (dispatch, getState) => {

    console.log("Get landing post was called")

    axios
        .get('http://127.0.0.1:8000/api/post-list/', tokenConfig(getState))
        .then(res => {
            dispatch({
                type: GET_POSTS,
                payload: res.data
            });
        }).catch(err => console.log(err));
}


// Create Post

export const createPost = (user, title, body)  => {

    console.log("Create post function called.");

    console.log("user id:", user);
	console.log("title is:", title);
	console.log("body is:", body);


    // Request Body
	const requestBody = JSON.stringify({ user, title, body });


    axios
      .post('http://127.0.0.1:8000/api/post-submit/', requestBody)
      .then((res) => {
        console.log(res)
      }).catch(err => console.log(err));

  };


