import axios from "axios";
import { GET_POSTS, CREATE_POST } from "./types";
import { tokenConfig } from './auth';

//Get Posts

export const getPosts = () => (dispatch, getState) => {
    axios
        .get('http://127.0.0.1:8000/api/post-list/', tokenConfig(getState))
        .then(res => {
            dispatch({
                type: GET_POSTS,
                payload: res.data
            });
        }).catch(err => console.log(err));
}


// Create Post

export const createPost = (post) => (dispatch, getState) => {
    axios
      .post('http://127.0.0.1:8000/api/post-list/', post, tokenConfig(getState))
      .then((res) => {
        dispatch({
          type: CREATE_POST,
          payload: res.data,
        });
      }).catch(err => console.log(err));

  };


