import React, { useEffect, useState } from "react";

import "./post.css";


const Post = () => {
    const [postsState, setPostsState] = useState([])

    useEffect(() => {
        fetch('http://127.0.0.1:8000/api/post-list/ ')
            .then((res)=>res.json())
            .then((postsArray) => {
                console.log(postsArray)
                // Map posts array. 
                const newPostsState = postsArray.map((post)=> {
                    return (
                        <div className="post-wrapper"> 
                            <div className="post">
                                <div className="post-sidebar">

                                </div>
                                <div className="post-mainbar">
                                    <div className="post-author">
                                            Posted by Username: {post.user}
                                    </div>
                                    <div className="post-title">
                                        {post.title}
                                    </div>
                                    <div className="post-body">
                                        {post.body}
                                    </div>
                                    <div className="post-footer">
                                        <div className="comments"></div>
                                    </div>
                                </div>

                            </div>
                         </div>

                    )


                })
                setPostsState(newPostsState)
            })
    }, [])

    const hasPosts = postsState.length > 0

    return <div>
        {postsState}
        </div>
}



export default Post;
