import React, {useState} from 'react'
import { Link, Redirect } from "react-router-dom";
import { connect } from 'react-redux';
import PropTypes from 'prop-types';

import LoginModal from "../loginModal/LoginModal";


import './Navbar.css'
import './Logo.css'
import './Searchbar.css'
import './Actions.css'

import SearchIcon from "@material-ui/icons/Search"
import Button from "../button/Button";
import PersonIcon from "@material-ui/icons/PersonOutline"
import ArrowDropDownIcon from "@material-ui/icons/ArrowDropDown";


export function Navbar({isAuthenticated}) {

    const [signInState, setsignInState] = useState({
        open: false,
        signInType : ""
    })

    const handleLogin = () => {
        setsignInState ({
            open: true, 
            signInType: "login"
        })
    }

    const handleRegister = () => {
        setsignInState ({
            open: true, 
            signInType: "register"
        })
    }


    if (isAuthenticated) {
        return (
            <div>
                <div className="navbar">

                            <div className="logo hoverable">
                                <Link to="/">
                                    <img src="./assets/images/reddit_logo_red.png"/>
                                </Link>
                                    <span>reddit</span>
                            </div>

                            <div className="searchBar">
                                <label htmlFor="searchBar">
                                    <SearchIcon/>
                                </label>
                                <input id="searchBar" placeholder="Search Reddit"></input>
                            </div>

                            <div className="actions">
                                <div className="profile ">
                                    {}
                                    <PersonIcon className="hoverable"/>
                                    <ArrowDropDownIcon className="hoverable"/>
                                </div>
                            </div>
                </div>
                <Redirect to="/homepage" />
            </div>

        )
    } else {

        return (
            <div>
                    <div className="navbar">

                                <div className="logo hoverable">
                                    <Link to="/">
                                        <img src="./assets/images/reddit_logo_red.png"/>
                                    </Link>
                                        <span>reddit</span>
                                </div>

                                <div className="searchBar">
                                    <label htmlFor="searchBar">
                                        <SearchIcon/>
                                    </label>
                                    <input id="searchBar" placeholder="Search Reddit"></input>
                                </div>

                                <div className="actions">
                                    <Button onClick={handleLogin} type="button" buttonStyle="btn--secondary" buttonSize="btn--small">Log In</Button>
                                    <Button onClick={handleRegister} type="button" buttonStyle="btn--primary" buttonSize="btn--small">Sign Up</Button>
                                    <div className="profile ">
                                        <PersonIcon className="hoverable"/>
                                        <ArrowDropDownIcon className="hoverable"/>
                                    </div>
                                </div>
                    </div>

                    <LoginModal open={signInState.open} action={signInState.signInType}  onClose={() => setsignInState({open: false})}>

                    </LoginModal>
                </div>
        )
    }


}


function mapStateToProps(state) {
return { isAuthenticated: state.auth.isAuthenticated };
} 


export default connect(mapStateToProps)(Navbar);
