import axios from "axios";
import { GET_POSTS, CREATE_POST, LIKE_POST, GET_FOLLOWING_POSTS } from "./types";
import { tokenConfig } from './auth';

//Get All Posts

export const getPosts = () => (dispatch, getState) => {

    console.log("Get landing post was called")

    axios
        .get('http://127.0.0.1:8000/api/post-list/', tokenConfig(getState))
        .then(res => {
            dispatch({
                type: GET_POSTS,
                payload: res.data
            });
        }).catch(err => console.log(err));

}


//Get All Posts form users the user follows

export const getFollowerPosts = (user_id) => (dispatch, getState) => {

    console.log("Get landing post was called")

    axios
        .get(`http://127.0.0.1:8000/api/post-list-followers/${user_id}`, tokenConfig(getState))
        .then(res => {
            dispatch({
                type: GET_FOLLOWING_POSTS,
                payload: res.data
            });
        }).catch(err => console.log(err));

}


// Create Post

export const createPost = (user, title, body) => (dispatch, getState) => {

    console.log("Create post function called.");

    // Request Body
	const requestBody = JSON.stringify({ user, title, body });


    axios
      .post('http://127.0.0.1:8000/api/post-submit/', requestBody, tokenConfig(getState))
      .then((res) => {
        dispatch({
          type: CREATE_POST,
          payload: res.data,
        });
      }).catch(err => console.log(err));

  };


// Like Post

export const likePost = (post) => (dispatch, getState) => {

    console.log("Upvote button clicked")
    console.log(post)

    post.likes = parseFloat(post.likes) + 1;

    console.log("Post for PUT request")

    axios
        .put('http://127.0.0.1:8000/api/post-submit/', post, tokenConfig(getState) )
        .then((res) => {
            dispatch({
                type: LIKE_POST,
                payload: res.data,
            });
        }).catch(err => console.log(err));

};


