import { GET_POSTS, CREATE_POST } from "../actions/types";

const initialState = {
	posts: [],
};

export default function (state = initialState, action) {
	switch (action.type) {
		case GET_POSTS:
			return {
				...state,
				posts: action.payload,
			};
        case GET_POSTS:
            return {
                ...state,
                allposts: action.payload,
            };
		case CREATE_POST:
			return {
				...state,
				posts: [...state.posts, action.payload],
			};
		default:
			return state;
	}
}
