import axios from "axios";
import { CREATE_POST } from "./types";

// Test Function 

export const testFunction = (title, body) => {
    console.log("The test function executed")
    console.log("title is:", title)

    return ("This is a bloody test function")
}

// Create Post

export const createPost = (title, body) => (dispatch) => {
	console.log("Create post function executed.");

	console.log("title is:", title);
	console.log("body is:", body);

	const config = {
		headers: {
			"Content-Type": "application/json",
		},
	};

	// Request Body
	const requestBody = JSON.stringify({ title, body });

	axios
		.get("http://127.0.0.1:8000/api/post-submit/", requestBody, config)
		.then((res) => {
			dispatch({
				type: CREATE_POST,
				payload: res.data,
			});
		})
		.catch((err) => console.log(err));
};
