import React, { Component } from 'react'

import './Navbar.css'
import Logo from './logo/Logo'
import './Logo.css'
import Searchbar from './searchbar/Searchbar'
import Actions from './actions/Actions'
import LoginModal from "../LoginModal/LoginModal";
import { Link } from "react-router-dom";
import './Searchbar.css'
import SearchIcon from "@material-ui/icons/Search"
import Button from "../button/Button";
import PersonIcon from "@material-ui/icons/PersonOutline"
import ArrowDropDownIcon from "@material-ui/icons/ArrowDropDown";



export default class Navbar extends Component {

    state = {
		modalOpen: true
	}

	handleModalOpen = () => {
		this.setState((prevState) => {
			return {
				modalOpen: !prevState.modalOpen
			}
		})
	}

    render() {
        return (
            <div>
                <div className="navbar">

                            <div className="logo hoverable">
                                <Link to="/">
                                    <img src="./assets/images/reddit_logo_red.png"/>
                                </Link>
                                    <span>reddit</span>
                            </div>

                            <div className="searchBar">
                                <label htmlFor="searchBar">
                                    <SearchIcon/>
                                </label>
                                <input id="searchBar" placeholder="Search Reddit"></input>
                            </div>

                            <div className="actions">
                                <Button onClick={this.handleModalOpen} type="button" buttonStyle="btn--secondary" buttonSize="btn--small">Log In</Button>
                                <Button type="button" buttonStyle="btn--primary" buttonSize="btn--small">Sign Up</Button>
                                <div className="profile ">
                                    <PersonIcon className="hoverable"/>
                                    <ArrowDropDownIcon className="hoverable"/>
                                </div>
                            </div>
                </div>
                <LoginModal
                modalOpen={this.state.modalOpen}
                handleModalOpen={this.handleModalOpen}
                />
            </div>
        )
    }
}
